// Extract data from Notion webhook
const data = $input.first().json;

// Get supplier info
const supplierName = data.properties.Supplier?.select?.name || 
                     data.properties.Supplier?.rich_text?.[0]?.text?.content;

// Get order number for matching
const orderNumber = data.properties['Order Number']?.rich_text?.[0]?.text?.content ||
                    data.properties.order_number?.rich_text?.[0]?.text?.content;

// Get additional metadata
const notionPageId = data.id;
const updatedAt = new Date().toISOString();

return [{
  json: {
    supplier_name: supplierName,
    order_number: orderNumber,
    notion_page_id: notionPageId,
    updated_at: updatedAt,
    // Clean order number for SQL search
    clean_order_number: orderNumber?.replace(/[^a-zA-Z0-9-_]/g, '')
  }
}];
