const items = $input.all().map(item => item.json);

const formattedItems = items.slice(0, 10).map((item, index) => {
  const urgency = item.stock_count === 1 ? 'üî¥ CRITICAL' : 'üü° LOW';
  return `${urgency} **${item.name}** (${item.brand})\n   üì¶ Stock: ${item.stock_count} | üìà Sales (30d): ${item.recent_sales} | üí∞ Avg Cost: ‚Ç¨${parseFloat(item.avg_cost).toFixed(2)}`;
}).join('\n\n');

const criticalItems = items.filter(i => i.stock_count === 1).length;

const embed = {
  title: '‚ö†Ô∏è Low Stock Alert',
  description: `**${items.length} products** are running low on stock`,
  color: 16776960, // Yellow
  fields: [
    {
      name: 'üìä Summary',
      value: `Total Products: **${items.length}**\nCritical (1 item): **${criticalItems}**`,
      inline: false
    },
    {
      name: 'üì¶ Top Items',
      value: formattedItems
    },
    {
      name: 'üí° Recommendation',
      value: 'Consider restocking these popular items to avoid lost sales',
      inline: false
    }
  ],
  timestamp: new Date().toISOString(),
  footer: {
    text: 'SoleFlipper Low Stock Monitor'
  }
};

return {
  json: {
    embeds: [embed]
  }
};
