// Format low stock items
const items = $input.all().map(item => item.json);

const formattedItems = items.slice(0, 10).map(item => {
  const urgency = item.stock_count === 1 ? 'üî¥ CRITICAL' : 'üü° LOW';
  return `${urgency} *${item.name}* (${item.brand})\n  üì¶ Stock: ${item.stock_count} | üìà Sales (30d): ${item.recent_sales} | üí∞ Avg Cost: ‚Ç¨${parseFloat(item.avg_cost).toFixed(2)}`;
}).join('\n\n');

return {
  json: {
    total_products: items.length,
    critical_items: items.filter(i => i.stock_count === 1).length,
    items_list: formattedItems
  }
};

