// Calculate price differences
const items = $input.all();
const alerts = [];

for (const item of items) {
  const data = item.json;

  // Skip if no market price returned
  if (!data.market_price || data.market_price === 0) continue;

  const currentPrice = parseFloat(data.current_price);
  const marketPrice = parseFloat(data.market_price);
  const purchaseCost = parseFloat(data.purchase_cost);

  // Calculate differences
  const priceDiff = ((currentPrice - marketPrice) / marketPrice * 100).toFixed(1);
  const profitMargin = ((currentPrice - purchaseCost) / currentPrice * 100).toFixed(1);

  // Alert if price is >10% above market or <5% profit margin
  if (Math.abs(priceDiff) > 10 || profitMargin < 5) {
    alerts.push({
      product_name: data.name,
      brand: data.brand,
      size: data.size,
      condition: data.condition,
      current_price: currentPrice.toFixed(2),
      market_price: marketPrice.toFixed(2),
      purchase_cost: purchaseCost.toFixed(2),
      price_diff: priceDiff,
      profit_margin: profitMargin,
      inventory_id: data.inventory_id,
      alert_type: Math.abs(priceDiff) > 10 ? 'price_mismatch' : 'low_margin'
    });
  }
}

return alerts.map(alert => ({ json: alert }));

